arguments.argument:
serpentWorld = 1;
serpentLevel = 2;
cerberusWorld = 1;
cerberusLevel = 4;

draw_sprite(spr_progressScreen,0,x,y);

if (floor(gamePercent/100) > 0)
{
    draw_sprite(spr_numbers,floor(gamePercent/100),x,y+64);
    draw_sprite(spr_numbers,floor(gamePercent/10)-10*floor(gamePercent/100),x+64,y+64);
    draw_sprite(spr_numbers,floor(gamePercent)-10*floor(gamePercent/10),x+128,y+64);
} else if (floor(gamePercent/10) > 0)
{
    draw_sprite(spr_numbers,floor(gamePercent/10),x+64,y+64);
    draw_sprite(spr_numbers,floor(gamePercent)-10*floor(gamePercent/10),x+128,y+64);
} else
{
    draw_sprite(spr_numbers,floor(gamePercent),x+128,y+64);
}

if (global.levelCompletionsWest[serpentWorld,serpentLevel] == false)
{
    draw_sprite(spr_stoneSerpentIcon,0,x+192,y);
}

if (global.levelCompletionsSouth[cerberusWorld,cerberusLevel] == false)
{
    draw_sprite(spr_cerberusIcon,0,x+256,y);
}
