arguments.argument:
if (global.levelCompletions[level] == 0)
{
    sprite_index = spr_mapLevelUnfinished;
}

if ((global.levelCompletionsNorth[level] == 1 || north == 0) && (global.levelCompletionsWest[level] == 1 || west == 0) && (global.levelCompletionsEast[level] == 1 || east == 0) && (global.levelCompletionsSouth[level] == 1 || south == 0))
{
    sprite_index = spr_mapLevelComplete;
}
