arguments.argument:
if (!attackSelected)
{
    attackType = irandom(1);
    attackSelected = true;


}

if (attackType == 0)
{
    aD_x = 0;
    aD_y = 0;
    while (((aD_x == 0 && aD_y == 0) || (aD_x != 0 && aD_y != 0)) || (aD_x*64 + x >= room_width) || (aD_x*64 + x < 0) || (aD_y*64 + y >= room_height) || (aD_y*64 + y < 0))
    {
        aD_x = irandom(2)-1;
        aD_y = irandom(2)-1;
    }

    hspeed = aD_x*8;
    vspeed = aD_y*8;
    x += hspeed;
    y += vspeed;
    attackType = -1;
}



if (attackType == 1)
{
    aD_x = 0;
    aD_y = 0;
    while (aD_x == 0 && aD_y == 0)
    {
        aD_x = irandom(2)-1;
        aD_y = irandom(2)-1;
    }

    projectile = instance_create(x+aD_x*64,y+aD_y*64,obj_fireball);
    projectile.hspeed = aD_x*8;
    projectile.vspeed = aD_y*8;

    attackType = -1;
}

if (instance_number(obj_fireball) == 0 && attackType == -1 && hspeed == 0 && vspeed == 0)
{
    attackCompleted = true;
}

if (attackType == -1 && (x mod 64 == 0) && (y mod 64 == 0) && (hspeed != 0 || vspeed != 0))
{
    hspeed = 0;
    vspeed = 0;
}

if (changeBack)
{
    attackSelected = false;
    attackCompleted = false;
    changeBack = false;
    instance_change(obj_fireTurretIdle,false);
}
