arguments.argument:
if (x==64*floor(x/64) && y==64*floor(y/64) && (hspeed!=0 || vspeed!=0))
{
    if (left==-1 && (!scr_walkableBlockExists(x/64,y/64,-1,0) || !scr_isBlockType(x/64,y/64,5)))
    {
            vspeed=0;
            hspeed=0;
            image_speed=0;
            attackCompleted = true;
            left = 0;
    }

    if (right==1 && (!scr_walkableBlockExists(x/64,y/64,1,0) || !scr_isBlockType(x/64,y/64,5)))
    {
        vspeed=0;
        hspeed=0;
        image_speed=0;
        attackCompleted = true;
        right = 0;
    }

    if (up==-1 && (!scr_walkableBlockExists(x/64,y/64,0,-1) || !scr_isBlockType(x/64,y/64,5)))
    {
        vspeed=0;
        hspeed=0;
        image_speed=0;
        attackCompleted = true;
        up = 0;
    }

    if (down==1 && (!scr_walkableBlockExists(x/64,y/64,0,1) || !scr_isBlockType(x/64,y/64,5)))
    {
        vspeed=0;
        hspeed=0;
        image_speed=0;
        attackCompleted = true;
        down = 0;
    }
}

if (arrowShot == true && instance_number(obj_arrow) == 0)
{
    attackCompleted = true;
    arrowShot = false;
