arguments.argument:
x = view_xview[0] + 240;
y = view_yview[0] + 240;

for (i=0; i<3; i+=1)
{
    for (j=0; j<3; j+=1)
    {
        buttons[i,j].x = x + 48*i + 16;
        buttons[i,j].y = y + 48*j + 16;
    }
}

if (buttons[0].clicked)
{
    for (i=0; i<3; i+=1)
    {
        for (j=0; j<3; j+=1)
        {
            with (buttons[i,j])
            {
                instance_destroy();
            }
        }
    }
    instance_change(obj_battleMenuMove,true);
}
