arguments.argument:
/* These two loops set up the block network's array
 * with all of the random block objects strewn about.
 *
 * We will likely move these to the creation event later on.
 */
if (obj_blockEmpty.instance_count > 0)
{
    with (obj_blockEmpty)
    {
        obj_blockNetwork.blocks[(x-obj_blockNetwork.x)/64,(y-obj_blockNetwork.y)/64] = 0;
        instance_destroy();
    }
}

if (obj_blockBasic.instance_count > 0)
{
    with (obj_blockBasic)
    {
        obj_blockNetwork.blocks[(x-obj_blockNetwork.x)/64,(y-obj_blockNetwork.y)/64] = type + 1;
        instance_destroy();
    }
}

scr_setUpBlockNetworkList();
