arguments.argument:
//who in their right mind would draw everything in the world at the same time even when 90% cannot be seen....
//........
//oh yeah, I did!
for (i=floor(view_xview[0]/64); i<hblocks && i<=floor((view_wview[0]+view_xview[0])/64); i+=1)
{
    for (j=floor(view_yview[0]/64); j<vblocks && j<=floor((view_hview[0]+view_yview[0])/64); j+=1)
    {
        if (blocks[i,j] < 1000)
        {
            draw_sprite(blockTextures[blocks[i,j]],animationFrame,64*i,64*j);
        } else if (blocks[i,j] < 1000000)
        {
            draw_sprite(spr_tileDarkness,0,64*i,64*j);
        } else
        {
            draw_sprite(spr_tileSet,blocks[i,j] mod 1000,64*i,64*j);
        }
    }
}

